string pzlInput = "";

part1();
part2();

void part1()
{
    char[] charArr = pzlInput.ToCharArray();
    char[] temp = new char[4];
    char[] distinct;


    for (int i = 3; i < charArr.Length; i++)
    {
        temp[0] = charArr[i - 3];
        temp[1] = charArr[i - 2];
        temp[2] = charArr[i - 1];
        temp[3] = charArr[i];

        distinct = temp.Distinct().ToArray();
        if (distinct.Length == 4)
        {
            Console.Write((i + 1) + ": ");
            Console.WriteLine(distinct);
            break;
        }

    }

    Console.ReadKey();
}

